import { useState, useEffect } from "react";
import "./shiftroom.css";
import ParallaxLayer from "./ParallaxLayer";
import ParallaxLayerVideo from "./ParallaxLayerVideo";
import GameModal from "./GameModal";
import Leaderboard from "./Leaderboard";
import AuthModal from "./AuthModal";
import AboutModal from "./AboutModal";

export default function ShiftRoom() {
  const [gameOpen, setGameOpen] = useState(false);
  const [authOpen, setAuthOpen] = useState(false);
  const [nickname, setNickname] = useState("");
  const [aboutOpen, setAboutOpen] = useState(false);

  // HOVER STATES
  const [hoverAbout, setHoverAbout] = useState(false);
  const [hoverPlay, setHoverPlay] = useState(false);
  const [hoverMint, setHoverMint] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem("shift_username");
    if (saved) setNickname(saved);
  }, []);

  function handleGameClick() {
    const token = localStorage.getItem("shift_token");
    if (!token) setAuthOpen(true);
    else setGameOpen(true);
  }

  return (
    <div className="scene-wrapper">

      {/* WORKER BADGE */}
      <div className="name-badge">
        üë∑ Worker: <span>{nickname || ""}</span>

        {nickname ? (
          <button
            className="logout-btn"
            onClick={() => {
              localStorage.removeItem("shift_token");
              localStorage.removeItem("shift_username");
              window.location.reload();
            }}
          >
            üö™ Logout
          </button>
        ) : (
          <button className="logout-btn" onClick={() => setAuthOpen(true)}>
            üîê Login
          </button>
        )}
      </div>

      {/* ============================ */}
      {/*          TOP MENU            */}
      {/* ============================ */}

      <div className="top-menu">

        {/* ABOUT TV */}
        <div
          className="tv-screen"
          onMouseEnter={() => {
            setHoverAbout(true);
            const hv = document.getElementById("aboutHover");
            hv.currentTime = 0;
            hv.play();
          }}
          onMouseLeave={() => {
            setHoverAbout(false);
            const hv = document.getElementById("aboutHover");
            hv.pause();
          }}
          onClick={() => setAboutOpen(true)}
        >
          <video
            id="aboutIdle"
            src="/About.webm"
            className={`tv-video ${hoverAbout ? "hidden" : ""}`}
            autoPlay
            loop
            muted
            playsInline
          />
          <video
            id="aboutHover"
            src="/AboutHover.webm"
            className={`tv-video ${hoverAbout ? "visible" : ""}`}
            muted
            playsInline
          />
        </div>

        {/* PLAY TV */}
        <div
          className="tv-screen"
          onMouseEnter={() => {
            setHoverPlay(true);
            const hv = document.getElementById("playHover");
            hv.currentTime = 0;
            hv.play();
          }}
          onMouseLeave={() => {
            setHoverPlay(false);
            const hv = document.getElementById("playHover");
            hv.pause();
          }}
          onClick={handleGameClick}
        >
          <video
            id="playIdle"
            src="/Play.webm"
            className={`tv-video ${hoverPlay ? "hidden" : ""}`}
            autoPlay
            loop
            muted
            playsInline
          />
          <video
            id="playHover"
            src="/PlayHover.webm"
            className={`tv-video ${hoverPlay ? "visible" : ""}`}
            muted
            playsInline
          />
        </div>

        {/* MINT TV */}
        <div
          className="tv-screen"
          onMouseEnter={() => {
            setHoverMint(true);
            const hv = document.getElementById("mintHover");
            hv.currentTime = 0;
            hv.play();
          }}
          onMouseLeave={() => {
            setHoverMint(false);
            const hv = document.getElementById("mintHover");
            hv.pause();
          }}
          onClick={() => console.log("Mint clicked")}
        >
          <video
            id="mintIdle"
            src="/Mint.webm"
            className={`tv-video ${hoverMint ? "hidden" : ""}`}
            autoPlay
            loop
            muted
            playsInline
          />
          <video
            id="mintHover"
            src="/MintHover.webm"
            className={`tv-video ${hoverMint ? "visible" : ""}`}
            muted
            playsInline
          />
        </div>

      </div>

      {/* POSTER */}
      <a
        href="https://x.com/i/communities/1958972752079008236"
        target="_blank"
        rel="noopener noreferrer"
        className="poster-container"
      >
        <img src="/X.png" className="poster-default" alt="X" />
        <img src="/Xhover.png" className="poster-hover" alt="X Hover" />
      </a>

      {/* PARALLAX SCENE */}
      <div className="scene">

        <ParallaxLayerVideo
          src="/Back.webm"
          intensityX={80}
          intensityY={40}
          invertX={false}
          invertY={false}
        />

        <img src="/Wall.png" className="layer-mid" alt="Wall Layer" />

        <ParallaxLayer
          src="/Shifter.png"
          speed={0.01}
          invert={true}
          className="door-layer"
        />

        <ParallaxLayer
          src="/Front.png"
          speed={0.04}
          invert={false}
          className="front"
        />

        <ParallaxLayer
          src="/Cup.png"
          speed={0.02}
          invert={false}
          className="counter-layer"
        />

        <ParallaxLayer
          src="/Order.png"
          speed={0.04}
          invert={false}
          className="counter-layer"
        />
      </div>

      {/* GAME MODAL */}
      <GameModal open={gameOpen} onClose={() => setGameOpen(false)} />

      {/* AUTH MODAL */}
      {authOpen && (
        <AuthModal
          onClose={() => setAuthOpen(false)}
          onSuccess={() => {
            setNickname(localStorage.getItem("shift_username"));
            setAuthOpen(false);
            setGameOpen(true);
          }}
        />
      )}

      <AboutModal open={aboutOpen} onClose={() => setAboutOpen(false)} />

      {/* LEADERBOARD */}
      <div className="lb-frame">
        <img src="/Leaderb.png" className="lb-frame-img" alt="Frame" />
        <div className="lb-content">
          <Leaderboard />
        </div>
      </div>

    </div>
  );
}
