import { useState, useEffect } from "react";
import "./shiftroom.css";
import ParallaxLayer from "./ParallaxLayer";
import GameModal from "./GameModal";
import Leaderboard from "./Leaderboard";
import AuthModal from "./AuthModal";   // <-- IMPORT AUTH MODAL

export default function ShiftRoom() {
  const [gameOpen, setGameOpen] = useState(false);
  const [authOpen, setAuthOpen] = useState(false);
  const [nickname, setNickname] = useState("");

  // Check if user is logged in before starting the game
  function handleGameClick() {
    const token = localStorage.getItem("shift_token");
    
    if (!token) {
      setAuthOpen(true);  // show login/register
    } else {
      setGameOpen(true);  // open game
    }
  }

  // Load username
  useEffect(() => {
    const saved = localStorage.getItem("shift_username");
    if (saved) setNickname(saved);
  }, []);

  return (
    <div className="scene-wrapper">

     
   {/* ðŸ”¥ NUME SUS DREAPTA */}
{nickname && (
  <div className="name-badge">
      ðŸ‘· Worker: <span>{nickname}</span>

      <button className="logout-btn" onClick={handleLogout}>
        ðŸšª Logout
      </button>
  </div>
)}


      {/* TOP MENU */}
      <div className="top-menu">

        {/* ABOUT */}
        <img 
          src="/About.png" 
          className="menu-item" 
          alt="About" 
        />

        {/* GALLERY â†’ GAME */}
        <img
          src="/Gallery.png"
          className="menu-item"
          alt="Game"
          onClick={handleGameClick}      // <-- IMPORTANT
          style={{ cursor: "pointer" }}
        />

        {/* MINT */}
        <img 
          src="/Mint.png" 
          className="menu-item" 
          alt="Mint" 
        />
      </div>

      {/* SCENE PARALLAX */}
      <div className="scene">
        <ParallaxLayer src="/Back.png" speed={0.015} invert={true} />
        <img src="/Wall.png" className="layer-mid" alt="middle layer" />
        <ParallaxLayer src="/Front.png" speed={0.04} className="front" />
      </div>

      {/* AUTH MODAL */}
      <AuthModal
        open={authOpen}
        onClose={() => setAuthOpen(false)}
        onSuccess={() => {
          setAuthOpen(false);
          setNickname(localStorage.getItem("shift_username") || "");
          setGameOpen(true);
        }}
      />

      {/* GAME MODAL */}
      <GameModal 
        open={gameOpen} 
        onClose={() => setGameOpen(false)} 
      />

      <Leaderboard />
    </div>
  );
}
