import { useEffect, useState } from "react";

export default function ParallaxLayer({
  src,
  speed = 0.02,
  invert = false,
  follow = null,   // follow offset din alt layer
  lockY = false,   // să nu se miște pe verticală
  lockX = false,   // să nu se miște pe orizontală
  className = ""
}) {
  const [pos, setPos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    // dacă layer-ul urmărește alt layer → nu calculăm mouse-ul
    if (follow) {
      follow((newPos) => setPos(newPos));
      return;
    }

    const move = (e) => {
      let x = (e.clientX - window.innerWidth / 2) * speed;
      let y = (e.clientY - window.innerHeight / 2) * speed;

      if (invert) {
        x = -x;
        y = -y;
      }

      if (lockX) x = 0;
      if (lockY) y = 0;

      setPos({ x, y });
    };

    window.addEventListener("mousemove", move);
    return () => window.removeEventListener("mousemove", move);
  }, [speed, invert, follow, lockX, lockY]);

  return (
    <img
      src={src}
      className={`parallax-layer ${className}`}
      alt=""
      style={{
        transform: `translate(${pos.x}px, ${pos.y}px)`,
      }}
    />
  );
}
