import { useState, useEffect, useRef } from "react";
import "./shiftroom.css";
import ParallaxLayer from "./ParallaxLayer";
import ParallaxLayerVideo from "./ParallaxLayerVideo";
import GameModal from "./GameModal";
import Leaderboard from "./Leaderboard";
import AuthModal from "./AuthModal";
import AboutModal from "./AboutModal";

export default function ShiftRoom() {
  const [gameOpen, setGameOpen] = useState(false);
  const [authOpen, setAuthOpen] = useState(false);
  const [nickname, setNickname] = useState("");
  const [aboutOpen, setAboutOpen] = useState(false);

  // REFS pentru televizoare
  const aboutVideoRef = useRef(null);
  const playVideoRef = useRef(null);
  const mintVideoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);


  useEffect(() => {
    const saved = localStorage.getItem("shift_username");
    if (saved) setNickname(saved);
  }, []);

  useEffect(() => {
    const gif = document.querySelector(".shifter-gif");
  
    function move(e) {
      const x = (e.clientX - window.innerWidth / 2) * 0.01;
      gif.style.transform = `translateX(-50%) translateY(${x}px)`;
    }
  
    window.addEventListener("mousemove", move);
    return () => window.removeEventListener("mousemove", move);
  }, []);
  

  function handleGameClick() {
    const token = localStorage.getItem("shift_token");
    if (!token) setAuthOpen(true);
    else setGameOpen(true);
  }

  function playShifterSound() {
    const sounds = [
      "/sounds/1.mp3",
      "/sounds/2.mp3",
      "/sounds/3.mp3",
      "/sounds/4.mp3",
    ];
    function playBackgroundMusic() {
      const audio = new Audio("/sounds/background.mp3");
      audio.loop = true;
      audio.volume = 0.35;
    
      audio.play().catch(err => console.log("Autoplay blocked:", err));
    
      // salvƒÉm √Æntr-o variabilƒÉ globalƒÉ pentru acces ulterior
      window.shiftMusic = audio;
    }
    
  
    const random = sounds[Math.floor(Math.random() * sounds.length)];
    const audio = new Audio(random);
    audio.volume = 0.7; // ajustezi c√¢t de tare sƒÉ fie
    audio.play();
  }
  

  // helpers pentru hover
  function playHover(ref, idleSrc, hoverSrc, isEnter) {
    const v = ref.current;
    if (!v) return;
    if (isEnter) {
      v.src = hoverSrc;
      v.loop = false;
    } else {
      v.src = idleSrc;
      v.loop = true;
    }
    v.currentTime = 0;
    v.play().catch(() => {});
  }

  return (
    
    <div className="scene-wrapper">

{/* MUTE BUTTON ‚Äì RIGHT BOTTOM */}
<div
  className="sound-toggle"
  onClick={() => {
    if (!window.shiftMusic) return;
  
    if (window.shiftMusic.paused) {
      window.shiftMusic.play();
      setIsMuted(false);
    } else {
      window.shiftMusic.pause();
      setIsMuted(true);
    }
  }}
  
>
  {window.shiftMusic && !window.shiftMusic.paused ? "üîä" : "üîá"}
</div>

      {/* WORKER BADGE */}
      <div className="name-badge">
        üë∑ Worker: <span>{nickname || ""}</span>

        {nickname ? (
          <button
            className="logout-btn"
            onClick={() => {
              localStorage.removeItem("shift_token");
              localStorage.removeItem("shift_username");
              window.location.reload();
            }}
          >
            üö™ Logout
          </button>
        ) : (
          <button className="logout-btn" onClick={() => setAuthOpen(true)}>
            üîê Login
          </button>
        )}
      </div>

      {/* TOP MENU ‚Äì 3 TELEVIZOARE */}
      <div className="top-menu">

        {/* ABOUT TV */}
        <div
          className="tv-screen"
          onMouseEnter={() =>
            playHover(aboutVideoRef, "/About.webm", "/AboutHover.webm", true)
          }
          onMouseLeave={() =>
            playHover(aboutVideoRef, "/About.webm", "/AboutHover.webm", false)
          }
          onClick={() => setAboutOpen(true)}
        >
          <video
            ref={aboutVideoRef}
            className="menu-video"
            src="/About.webm"
            autoPlay
            loop
            muted
            playsInline
            onError={(e) =>
              console.error("VIDEO ERROR:", e.target.src)
            }
          />
        </div>

        {/* PLAY TV */}
        <div
          className="tv-screen"
          onMouseEnter={() =>
            playHover(playVideoRef, "/About.webm", "/AboutHover.webm", true)
          }
          onMouseLeave={() =>
            playHover(playVideoRef, "/About.webm", "/AboutHover.webm", false)
          }
          onClick={handleGameClick}
        >
          <video
            ref={playVideoRef}
            className="menu-video"
            src="/Play.webm"
            autoPlay
            loop
            muted
            playsInline
            onError={(e) =>
              console.error("VIDEO ERROR:", e.target.src)
            }
          />
        </div>

        {/* MINT TV */}
        <div
          className="tv-screen"
          onMouseEnter={() =>
            playHover(mintVideoRef, "/Mint.webm", "/MintHover.webm", true)
          }
          onMouseLeave={() =>
            playHover(mintVideoRef, "/Mint.webm", "/MintHover.webm", false)
          }
          onClick={() => console.log("Mint clicked")}
        >
          <video
            ref={mintVideoRef}
            className="menu-video"
            src="/Mint.webm"
            autoPlay
            loop
            muted
            playsInline
            onError={(e) =>
              console.error("VIDEO ERROR:", e.target.src)
            }
          />
        </div>

      </div>

      {/* POSTER RIGHT */}
      <a
        href="https://x.com/i/communities/1958972752079008236"
        target="_blank"
        rel="noopener noreferrer"
        className="poster-container"
      >
        <img src="/X.png" className="poster-default" alt="X" />
        <img src="/Xhover.png" className="poster-hover" alt="X Hover" />
      </a>

      {/* PARALLAX SCENE */}
      <div className="scene">
        <ParallaxLayerVideo
          src="/Back.webm"
          intensityX={80}
          intensityY={40}
          invertX={false}
          invertY={false}
        />

        <img src="/Wall.png" className="layer-mid" alt="Wall Layer" />

        <img
  src="/Shifter.gif"
  className="shifter-gif"
  alt="Shifter"
  onClick={playShifterSound}
/>








        <ParallaxLayer
          src="/Front.png"
          speed={0.04}
          invert={false}
          className="front"
        />

        <ParallaxLayer
          src="/Cup.png"
          speed={0.02}
          invert={false}
          className="counter-layer"
        />

        <ParallaxLayer
          src="/Order.png"
          speed={0.04}
          invert={false}
          className="counter-layer"
        />
      </div>

      {/* GAME MODAL */}
      <GameModal open={gameOpen} onClose={() => setGameOpen(false)} />

      {/* AUTH MODAL */}
      {authOpen && (
        <AuthModal
          onClose={() => setAuthOpen(false)}
          onSuccess={() => {
            setNickname(localStorage.getItem("shift_username"));
            setAuthOpen(false);
            setGameOpen(true);
          }}
        />
      )}

      {/* ABOUT MODAL */}
      <AboutModal open={aboutOpen} onClose={() => setAboutOpen(false)} />

      {/* LEADERBOARD + FRAME */}
      <div className="lb-frame">
        <img src="/Leaderb.png" className="lb-frame-img" alt="Frame" />
        <div className="lb-content">
          <Leaderboard />
        </div>
      </div>

    </div>
  );
}
