import { useEffect, useState } from "react";

export default function ParallaxLayerVideo({
  src,
  speed = 0.1,         // 游댠 mult mai mare dec칙t la imagini
  intensity = 60,       // c칙t se deplaseaz캒 video-ul
  invert = false,
  className = ""
}) {
  const [offset, setOffset] = useState(0);

  useEffect(() => {
    function handleMouseMove(e) {
      const x = e.clientX / window.innerWidth;
      const factor = invert ? (x - 0.5) : -(x - 0.5);

      // 游댠 mi탳care mult mai vizibil캒 pentru video
      const movement = factor * intensity;

      // 游댠 easing smooth, ca parallax real
      setOffset(movement);
    }

    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, [intensity, invert]);

  return (
    <video
      className={`parallax-video ${className}`}
      autoPlay
      loop
      muted
      playsInline
      style={{
        transform: `translateX(${offset}px)`,
        transition: "transform 0.15s ease-out"  // 游댠 senzatie reala de parallax
      }}
    >
      <source src={src} type="video/webm" />
    </video>
  );
}
