import { useState, useEffect } from "react";
import "./loading.css";

export default function LoadingScreen({ onLoaded }) {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const assets = [
      "/Wall.png",
      "/Front.png",
      "/Shifter.gif",
      "/Back.webm",
      "/About.webm",
      "/AboutHover.webm",
      "/play.webm",
      "/game.webm",
      "/mint.webm",
      "/minthover.webm"
    ];

    let loaded = 0;

    assets.forEach((src) => {
      const isVideo = src.endsWith(".webm") || src.endsWith(".mp4");
      const element = isVideo ? document.createElement("video") : new Image();
      element.src = src;

      element.onload = element.onloadeddata = () => {
        loaded++;
        const pct = Math.round((loaded / assets.length) * 100);
        setProgress(pct);

        if (pct === 100) {
          setTimeout(onLoaded, 500);
        }
      };
    });
  }, []);

  return (
    <div className="loading-circle-container">
      <div className="loading-ring">
        {[...Array(40)].map((_, i) => (
          <div
            key={i}
            className={`dot ${i < Math.floor((progress / 100) * 40) ? "active" : ""}`}
          ></div>
        ))}
      </div>

      <div className="loading-percent">{progress}%</div>
      <div className="loading-text">Preparing your shiftâ€¦</div>
    </div>
  );
}
