import { useState, useEffect } from "react";
import "./shiftroom.css";
import ParallaxLayer from "./ParallaxLayer";
import ParallaxLayerVideo from "./ParallaxLayerVideo";
import GameModal from "./GameModal";
import Leaderboard from "./Leaderboard";
import AuthModal from "./AuthModal";
import ClickableParallaxLayer from "./ClickableParallaxLayer";


export default function ShiftRoom() {
  const [gameOpen, setGameOpen] = useState(false);
  const [authOpen, setAuthOpen] = useState(false);
  const [nickname, setNickname] = useState("");
  const [frontOffset, setFrontOffset] = useState({ x: 0, y: 0 });

  function playSound(path) {
    const audio = new Audio(path);
    audio.volume = 1.0;
    audio.play();
  }
  


  useEffect(() => {
    const saved = localStorage.getItem("shift_username");
    if (saved) setNickname(saved);
  }, []);

  function handleGameClick() {
    const token = localStorage.getItem("shift_token");

    if (!token) setAuthOpen(true);
    else setGameOpen(true);
  }

  return (
    <div className="scene-wrapper">

      {/* Worker Badge */}
      <div className="name-badge">
        üë∑ Worker: <span>{nickname || ""}</span>

        {nickname ? (
          <button
            className="logout-btn"
            onClick={() => {
              localStorage.removeItem("shift_token");
              localStorage.removeItem("shift_username");
              window.location.reload();
            }}
          >
            üö™ Logout
          </button>
        ) : (
          <button
            className="logout-btn"
            onClick={() => setAuthOpen(true)}
          >
            üîê Login
          </button>
        )}
      </div>

      {/* TOP MENU */}
      <div className="top-menu">
        <img src="/About.png" className="menu-item" alt="About" />
        <img
          src="/Gallery.png"
          className="menu-item"
          alt="Game"
          onClick={handleGameClick}
          style={{ cursor: "pointer" }}
        />
        <img src="/Mint.png" className="menu-item" alt="Mint" />
      </div>

      {/* PARALLAX SCENE */}
      <div className="scene">

        {/* 1Ô∏è‚É£ BACKGROUND VIDEO */}
        <ParallaxLayerVideo
          src="/Back.webm"
          intensityX={80}
          intensityY={40}
          invertX={true}
          invertY={true}
        />

        {/* 2Ô∏è‚É£ WALL (static) */}
        <img src="/Wall.png" className="layer-mid" alt="middle layer" />

        {/* 3Ô∏è‚É£ DOOR ‚Äî very subtle parallax */}
        <ParallaxLayer
          src="/Shifter.png"
          speed={0.01}     // üî• foarte mic
          invert={true}
          className="door-layer"
        />

 

        {/* 5Ô∏è‚É£ FRONT ‚Äî strong parallax */}
        <ParallaxLayer
          src="/Front.png"
          speed={0.04}
          invert={false}
          className="front"
        />

               {/* 4Ô∏è‚É£ COUNTER ‚Äî medium parallax */}
{/* CUP - sincronizat cu FRONT */}
<ClickableParallaxLayer
  src="/Cup.png"
  speed={0.04}
  invert={false}
  className="counter-layer"
  onClick={() => playSound("/cup.wav")}
/>

{/* ORDER PAPER */}
<ClickableParallaxLayer
  src="/Order.png"
  speed={0.04}
  invert={false}
  className="counter-layer"
  onClick={() => playSound("/order.wav")}
/>


      </div>

      {/* GAME MODAL */}
      <GameModal open={gameOpen} onClose={() => setGameOpen(false)} />

      {/* AUTH MODAL */}
      {authOpen && (
        <AuthModal
          onClose={() => setAuthOpen(false)}
          onSuccess={() => {
            setNickname(localStorage.getItem("shift_username"));
            setAuthOpen(false);
            setGameOpen(true);
          }}
        />
      )}

      <Leaderboard />
    </div>
  );
}
