import { useEffect, useState } from "react";

export default function ParallaxLayer({
  src,
  speedX = 0.02,
  speedY = 0.02,
  invertX = false,
  invertY = false,
  follow = null,   // dacă vrei să copieze alt layer
  className = ""
}) {
  const [offset, setOffset] = useState({ x: 0, y: 0 });

  useEffect(() => {
    if (follow) {
      // COPIAZĂ MISCAREA ALTUI LAYER
      follow((parentOffset) => {
        setOffset(parentOffset);
      });
      return;
    }

    function handleMove(e) {
      const x = e.clientX / window.innerWidth;
      const y = e.clientY / window.innerHeight;

      const moveX = (invertX ? (x - 0.5) : -(x - 0.5)) * speedX * 100;
      const moveY = (invertY ? (y - 0.5) : -(y - 0.5)) * speedY * 100;

      setOffset({ x: moveX, y: moveY });
    }

    window.addEventListener("mousemove", handleMove);
    return () => window.removeEventListener("mousemove", handleMove);
  }, [speedX, speedY, invertX, invertY, follow]);

  return (
    <img
      src={src}
      className={`parallax-img ${className}`}
      style={{
        transform: `translate(${offset.x}px, ${offset.y}px)`
      }}
      alt=""
    />
  );
}
