import { useRef, useState } from "react";
import "./menuvideo.css";

export default function MenuVideo({
  srcIdle,     // video normal (loop)
  srcHover,    // video hover (play once)
  onClick
}) {
  const idleRef = useRef(null);
  const hoverRef = useRef(null);

  const [hovering, setHovering] = useState(false);

  function onMouseEnter() {
    setHovering(true);

    // pornește video-ul de hover de la început
    if (hoverRef.current) {
      hoverRef.current.currentTime = 0;
      hoverRef.current.play();
    }
  }

  function onMouseLeave() {
    setHovering(false);

    // oprește hover-ul
    if (hoverRef.current) {
      hoverRef.current.pause();
      hoverRef.current.currentTime = 0;
    }
  }

  return (
    <div
      className="menu-video-container"
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
      onClick={onClick}
    >
      {/* Idle looping video */}
      <video
        ref={idleRef}
        src={srcIdle}
        className={`menu-video ${hovering ? "hidden" : ""}`}
        autoPlay
        loop
        muted
        playsInline
      />

      {/* Hover video (plays once) */}
      <video
        ref={hoverRef}
        src={srcHover}
        className={`menu-video ${hovering ? "visible" : ""}`}
        muted
        playsInline
      />
    </div>
  );
}
