import { useEffect, useState } from "react";

export default function ParallaxLayerVideo({ src, speed = 0.02, invert = false, className = "" }) {
  const [offset, setOffset] = useState(0);

  useEffect(() => {
    function handleMouseMove(e) {
      const x = e.clientX / window.innerWidth;
      const movement = invert ? (x - 0.5) : -(x - 0.5);

      setOffset(movement * speed * 100); // same formula as image version
    }

    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, [speed, invert]);

  return (
    <video
      className={`parallax-video ${className}`}
      autoPlay
      loop
      muted
      playsInline
      style={{
        transform: `translateX(${offset}px)`,
      }}
    >
      <source src={src} type="video/webm" />
    </video>
  );
}
