import { useEffect, useState } from "react";

export default function ParallaxLayer({ src, speed = 0.02, invert = false, className = "" }) {
  const [pos, setPos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const move = (e) => {
      // ðŸ”¥ dacÄƒ jocul este deschis â†’ oprim tot parallax-ul
      if (window.__PARALLAX_DISABLED__) return;

      let x = (e.clientX - window.innerWidth / 2) * speed;
      let y = (e.clientY - window.innerHeight / 2) * speed;

      if (invert) {
        x = -x;
        y = -y;
      }

      setPos({ x, y });
    };

    window.addEventListener("mousemove", move);
    return () => window.removeEventListener("mousemove", move);
  }, [speed, invert]);

  // ðŸ”¥ dacÄƒ este oprit parallax-ul â†’ Ã®ngheaÈ›Äƒ poziÈ›ia (0,0)
  const appliedX = window.__PARALLAX_DISABLED__ ? 0 : pos.x;
  const appliedY = window.__PARALLAX_DISABLED__ ? 0 : pos.y;

  return (
    <img
      src={src}
      className={`parallax-layer ${className}`}
      alt=""
      style={{
        transform: `translate(${appliedX}px, ${appliedY}px) scale(1.05)`
      }}
    />
  );
}
