import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import "./intro.css";

export default function Intro({ onStart }) {
  const [name, setName] = useState("");

  useEffect(() => {
    const saved = localStorage.getItem("shift_username");
    if (saved) setName(saved);
  }, []);

  function generatePassword() {
    return Math.random().toString(36).slice(2, 10);
  }

  async function startShift() {
    if (!name.trim()) return;

    const password =
      localStorage.getItem("shift_password") || generatePassword();

    const res = await fetch("http://localhost:5000/api/auth/register-or-login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username: name.trim(), password })
    });

    const data = await res.json();
    if (!res.ok) {
      alert(data.error || "Login error");
      return;
    }

    // ✔ Salvare credențiale local
    localStorage.setItem("shift_username", name.trim());
    localStorage.setItem("shift_password", password);
    localStorage.setItem("shift_token", data.token);

    onStart();
  }

  return (
    <div className="intro-container">

      <motion.img
        src="/logo.gif"
        className="intro-logo"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      />

      <motion.h1
        className="intro-title"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 0.3 }}
      >
        Welcome back, worker.
      </motion.h1>

      <motion.input
        className="nickname-input"
        placeholder="Enter your username..."
        value={name}
        onChange={(e) => setName(e.target.value)}
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.6 }}
      />

      <motion.button
        className="intro-btn"
        onClick={startShift}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.8 }}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.92 }}
      >
        Start Your Shift
      </motion.button>

    </div>
  );
}
