import { useEffect, useState, useRef } from "react";

export default function ParallaxLayer({
  src,
  speed = 0.02,
  invert = false,
  smoothness = 0.1,   // ðŸ”¥ 0.05 = lent, 0.15 = rapid
  className = "",
}) {
  const [pos, setPos] = useState({ x: 0, y: 0 });
  const target = useRef({ x: 0, y: 0 });

  useEffect(() => {
    function handleMove(e) {
      let x = (e.clientX - window.innerWidth / 2) * speed;
      let y = (e.clientY - window.innerHeight / 2) * speed;

      if (invert) {
        x = -x;
        y = -y;
      }

      target.current = { x, y };
    }

    window.addEventListener("mousemove", handleMove);

    return () => window.removeEventListener("mousemove", handleMove);
  }, [speed, invert]);

  // ðŸ”¥ SMOOTH LERP ANIMATION
  useEffect(() => {
    function animate() {
      setPos((prev) => ({
        x: prev.x + (target.current.x - prev.x) * smoothness,
        y: prev.y + (target.current.y - prev.y) * smoothness,
      }));

      requestAnimationFrame(animate);
    }
    animate();
  }, [smoothness]);

  return (
    <img
      src={src}
      alt=""
      className={`parallax-layer ${className}`}
      style={{
        transform: `translate(${pos.x}px, ${pos.y}px)`,
      }}
    />
  );
}
