import { useState, useEffect } from "react";
import "./loading.css";

export default function LoadingScreen({ onLoaded }) {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const assets = [
      "/Wall.png",
      "/Front.png",
      "/Shifter.gif",
      "/Back.webm",
      "/About.webm",
      "/AboutHover.webm",
      "/play.webm",
      "/game.webm",
      "/mint.webm",
      "/minthover.webm"
    ];

    let loaded = 0;

    assets.forEach(src => {
      const element = src.endsWith(".webm") ? document.createElement("video") : new Image();
      element.src = src;

      element.onloadeddata = element.onload = () => {
        loaded++;
        setProgress(Math.round((loaded / assets.length) * 100));
        if (loaded === assets.length) {
          setTimeout(onLoaded, 500); // mic delay pt smoothness
        }
      };
    });
  }, []);

  return (
    <div className="loading-container">
      <div className="loading-title">Loading your shiftâ€¦</div>

      <div className="loading-bar">
        <div className="loading-fill" style={{ width: `${progress}%` }} />
      </div>

      <div className="loading-percent">{progress}%</div>
    </div>
  );
}
