import { useState, useEffect } from "react";

export default function ParallaxResponsiveLayer({
  src,
  speedX = 0.02,
  speedY = 0.02,
  invert = false,
  type = "image", // "image" sau "video"
  zIndex = 1
}) {
  const [scale, setScale] = useState(1);
  const [offset, setOffset] = useState({ x: 0, y: 0 });

  // ðŸ”¥ 1. RESPONSIVE SCALING
  useEffect(() => {
    function updateScale() {
      const w = window.innerWidth;
      const h = window.innerHeight;

      // raportul tÄƒu original
      const targetRatio = 1920 / 1080;
      const screenRatio = w / h;

      let s =
        screenRatio > targetRatio
          ? h / 1080   // Ã®nÄƒlÈ›imea dicteazÄƒ
          : w / 1920;  // lÄƒÈ›imea dicteazÄƒ

      setScale(s);
    }

    updateScale();
    window.addEventListener("resize", updateScale);
    return () => window.removeEventListener("resize", updateScale);
  }, []);

  // ðŸ”¥ 2. PARALLAX â€” X È™i Y
  useEffect(() => {
    function handleMove(e) {
      const x = e.clientX / window.innerWidth;
      const y = e.clientY / window.innerHeight;

      // invert dacÄƒ vrei adÃ¢ncime corectÄƒ
      const mx = invert ? (x - 0.5) : -(x - 0.5);
      const my = invert ? (y - 0.5) : -(y - 0.5);

      setOffset({
        x: mx * speedX * 100,
        y: my * speedY * 100
      });
    }

    window.addEventListener("mousemove", handleMove);
    return () => window.removeEventListener("mousemove", handleMove);
  }, [speedX, speedY, invert]);

  // ðŸ”¥ 3. ReturnÄƒm video sau imagine
  const commonStyle = {
    position: "absolute",
    top: "50%",
    left: "50%",
    width: "1920px",
    height: "1080px",
    transform: `translate(-50%, -50%) translate(${offset.x}px, ${offset.y}px) scale(${scale})`,
    objectFit: "cover",
    pointerEvents: "none",
    zIndex
  };

  return type === "video" ? (
    <video
      src={src}
      autoPlay
      loop
      muted
      playsInline
      style={commonStyle}
    />
  ) : (
    <img
      src={src}
      style={commonStyle}
      alt=""
    />
  );
}
