import { useState, useEffect } from "react";
import "./shiftroom.css";
import ParallaxLayer from "./ParallaxLayer";
import GameModal from "./GameModal";
import Leaderboard from "./Leaderboard";
import AuthModal from "./AuthModal";

export default function ShiftRoom() {
  const [gameOpen, setGameOpen] = useState(false);
  const [authOpen, setAuthOpen] = useState(false);
  const [nickname, setNickname] = useState("");

  useEffect(() => {
    const saved = localStorage.getItem("shift_username");
    if (saved) setNickname(saved);
  }, []);

  function handleGameClick() {
    const token = localStorage.getItem("shift_token");

    if (!token) {
      // user NU este logat ‚Üí deschidem AUTH MODAL
      setAuthOpen(true);
    } else {
      // user logat ‚Üí deschidem jocul
      setGameOpen(true);
    }
  }

  return (
    <div className="scene-wrapper">

      {/* üî• WORKER BADGE ALWAYS VISIBLE */}
      <div className="name-badge">
        üë∑ Worker: <span>{nickname ? nickname : "Guest"}</span>

        {nickname ? (
          // LOGOUT dacƒÉ este logat
          <button
            className="logout-btn"
            onClick={() => {
              localStorage.removeItem("shift_token");
              localStorage.removeItem("shift_username");
              window.location.reload();
            }}
          >
            üö™ Logout
          </button>
        ) : (
          // LOGIN dacƒÉ nu este logat
          <button
            className="logout-btn"
            onClick={() => setAuthOpen(true)}
          >
            üîê Login
          </button>
        )}
      </div>

      {/* TOP MENU */}
      <div className="top-menu">

        <img src="/About.png" className="menu-item" alt="About" />

        <img
          src="/Gallery.png"
          className="menu-item"
          alt="Game"
          onClick={handleGameClick}
          style={{ cursor: "pointer" }}
        />

        <img src="/Mint.png" className="menu-item" alt="Mint" />

      </div>

      {/* PARALLAX SCENE */}
      <div className="scene">
        <ParallaxLayer src="/Back.png" speed={0.015} invert={true} />
        <img src="/Wall.png" className="layer-mid" alt="middle layer" />
        <ParallaxLayer src="/Front.png" speed={0.04} className="front" />
      </div>

      {/* GAME MODAL */}
      <GameModal open={gameOpen} onClose={() => setGameOpen(false)} />

      {/* AUTH MODAL */}
      {authOpen && (
        <AuthModal
          onClose={() => setAuthOpen(false)}
          onSuccess={() => {
            const saved = localStorage.getItem("shift_username");
            if (saved) setNickname(saved);

            setAuthOpen(false);
            setGameOpen(true); // start game after login
          }}
        />
      )}

      <Leaderboard />
    </div>
  );
}
