import { useState, useEffect, useRef } from "react";
import "./shiftroom.css";

import ParallaxLayer from "./ParallaxLayer";
import ParallaxLayerVideo from "./ParallaxLayerVideo";
import GameModal from "./GameModal";
import Leaderboard from "./Leaderboard";
import AuthModal from "./AuthModal";
import AboutModal from "./AboutModal";

export default function ShiftRoom() {
  const [gameOpen, setGameOpen] = useState(false);
  const [authOpen, setAuthOpen] = useState(false);
  const [nickname, setNickname] = useState("");
  const [aboutOpen, setAboutOpen] = useState(false);

  // sound toggle
  const [isMuted, setIsMuted] = useState(false);

  // TV hover refs
  const aboutHoverRef = useRef(null);
  const playHoverRef = useRef(null);
  const mintHoverRef = useRef(null);

  useEffect(() => {
    const saved = localStorage.getItem("shift_username");
    if (saved) setNickname(saved);
  }, []);

  // shifter bobbing effect
  useEffect(() => {
    const gif = document.querySelector(".shifter-gif");
    if (!gif) return;

    function move(e) {
      const x = (e.clientX - window.innerWidth / 2) * 0.01;
      gif.style.transform = `translateX(-50%) translateY(${x}px)`;
    }

    window.addEventListener("mousemove", move);
    return () => window.removeEventListener("mousemove", move);
  }, []);

  function handleGameClick() {
    const token = localStorage.getItem("shift_token");
    if (!token) setAuthOpen(true);
    else setGameOpen(true);
  }

  function playShifterSound() {
    const sounds = [
      "/sounds/1.mp3",
      "/sounds/2.mp3",
      "/sounds/3.mp3",
      "/sounds/4.mp3",
    ];

    const random = sounds[Math.floor(Math.random() * sounds.length)];
    const audio = new Audio(random);
    audio.volume = 0.7;
    audio.play();
  }

  // -----------------------
  // SOUND TOGGLE
  // -----------------------
  function toggleSound() {
    if (!window.shiftMusic) return;

    if (window.shiftMusic.paused) {
      window.shiftMusic.play();
      setIsMuted(false);
    } else {
      window.shiftMusic.pause();
      setIsMuted(true);
    }
  }

  // -----------------------
  // TV HOVER HANDLER
  // -----------------------
  function handleHover(ref, enter) {
    if (!ref.current) return;
    ref.current.style.opacity = enter ? 1 : 0;
  }

  return (
    <div className="scene-wrapper">

      {/* SOUND TOGGLE */}
      <div className="sound-toggle" onClick={toggleSound}>
        <img
          src={isMuted ? "/sound_off.gif" : "/sound_on.gif"}
          className="sound-icon"
          alt="sound"
        />
      </div>

      {/* WORKER BADGE */}
      <div className="name-badge">
        üë∑ Worker: <span>{nickname || ""}</span>

        {nickname ? (
          <button
            className="logout-btn"
            onClick={() => {
              localStorage.removeItem("shift_token");
              localStorage.removeItem("shift_username");
              window.location.reload();
            }}
          >
            üö™ Logout
          </button>
        ) : (
          <button className="logout-btn" onClick={() => setAuthOpen(true)}>
            üîê Login
          </button>
        )}
      </div>

      {/* TOP TV MENU */}
      <div className="top-menu">

        {/* ABOUT TV */}
        <div
          className="tv-screen"
          onMouseEnter={() => handleHover(aboutHoverRef, true)}
          onMouseLeave={() => handleHover(aboutHoverRef, false)}
          onClick={() => setAboutOpen(true)}
        >
          <video className="menu-video idle" src="/About.webm" autoPlay loop muted playsInline />
          <video ref={aboutHoverRef} className="menu-video hover" src="/AboutHover.webm" autoPlay loop muted playsInline />
        </div>

        {/* PLAY TV */}
        <div
          className="tv-screen"
          onMouseEnter={() => handleHover(playHoverRef, true)}
          onMouseLeave={() => handleHover(playHoverRef, false)}
          onClick={handleGameClick}
        >
          <video className="menu-video idle" src="/game.webm" autoPlay loop muted playsInline />
          <video ref={playHoverRef} className="menu-video hover" src="/play.webm" autoPlay loop muted playsInline />
        </div>

        {/* MINT TV */}
        <div
          className="tv-screen"
          onMouseEnter={() => handleHover(mintHoverRef, true)}
          onMouseLeave={() => handleHover(mintHoverRef, false)}
          onClick={() => console.log("Mint clicked")}
        >
          <video className="menu-video idle" src="/mint.webm" autoPlay loop muted playsInline />
          <video ref={mintHoverRef} className="menu-video hover" src="/minthover.webm" autoPlay loop muted playsInline />
        </div>

      </div>

      {/* POSTER RIGHT */}
      <a
        href="https://x.com/i/communities/1958972752079008236"
        target="_blank"
        rel="noopener noreferrer"
        className="poster-container"
      >
        <img src="/X.png" className="poster-default" alt="X" />
        <img src="/Xhover.png" className="poster-hover" alt="X Hover" />
      </a>

      {/* PARALLAX SCENE */}
      <div className="scene">
        <ParallaxLayerVideo src="/Back.webm" intensityX={80} intensityY={40} />
        <img src="/Wall.png" className="layer-mid" alt="Wall Layer" />

        <img
          src="/Shifter.gif"
          className="shifter-gif"
          alt="Shifter"
          onClick={playShifterSound}
        />

        <ParallaxLayer src="/Front.png" speed={0.04} className="front" />
        <ParallaxLayer src="/Cup.png" speed={0.02} className="counter-layer" />
        <ParallaxLayer src="/Order.png" speed={0.04} className="counter-layer" />
      </div>

      {/* MODALS */}
      <GameModal open={gameOpen} onClose={() => setGameOpen(false)} />

      {authOpen && (
        <AuthModal
          onClose={() => setAuthOpen(false)}
          onSuccess={() => {
            setNickname(localStorage.getItem("shift_username"));
            setAuthOpen(false);
            setGameOpen(true);
          }}
        />
      )}

      <AboutModal open={aboutOpen} onClose={() => setAboutOpen(false)} />

      {/* LEADERBOARD */}
      <div className="lb-frame">
        <img src="/Leaderb.png" className="lb-frame-img" alt="Frame" />
        <div className="lb-content">
          <Leaderboard />
        </div>
      </div>
    </div>
  );
}
