import { useEffect, useState } from "react";
import "./leaderboard.css";

export default function Leaderboard() {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    load();
    const interval = setInterval(load, 3000);
    return () => clearInterval(interval);
  }, []);

  async function load() {
    const res = await fetch("http://localhost:5000/api/leaderboard");
    const data = await res.json();
    setScores(data);
  }

  const currentUser = localStorage.getItem("shift_username");

  return (
    <div className="lb-frame-wrapper">
      {/* Rama */}
      <img src="/Leaderb.png" className="lb-frame" alt="Leaderboard Frame" />

      {/* Con»õinutul din interiorul ramei */}
      <div className="lb-content">
        <h3 className="lb-title">üèÜ Leaderboard</h3>

        {scores.map((s, i) => (
          <div
            key={i}
            className={`lb-entry ${s.name === currentUser ? "me" : ""}`}
          >
            <span className="rank">{i + 1}.</span>
            <span className="user">{s.name}</span>
            <span className="score">{s.score}</span>
          </div>
        ))}
      </div>
    </div>
  );
}
