import { useEffect, useState } from "react";

export default function ParallaxLayerVideo({
  src,
  intensityX = 80,   // cât se mișcă pe X
  intensityY = 30,   // cât se mișcă pe Y
  invertX = false,   // inversare orizontală
  invertY = false,   // inversare verticală
  className = ""
}) {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [blur, setBlur] = useState(0);
  const [lastPos, setLastPos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    function handleMouseMove(e) {
      const x = e.clientX / window.innerWidth;
      const y = e.clientY / window.innerHeight;

      const moveX = (invertX ? (x - 0.5) : -(x - 0.5)) * intensityX;
      const moveY = (invertY ? (y - 0.5) : -(y - 0.5)) * intensityY;

      // CALCUL VITEZĂ → pt. motion blur
      const velocity = Math.sqrt(
        Math.pow(moveX - lastPos.x, 2) + Math.pow(moveY - lastPos.y, 2)
      );

      const blurAmount = Math.min(velocity * 0.1, 6); // blur max: 6px

      setBlur(blurAmount);
      setLastPos({ x: moveX, y: moveY });
      setOffset({ x: moveX, y: moveY });
    }

    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, [intensityX, intensityY, invertX, invertY, lastPos]);

  return (
    <video
      className={`parallax-video ${className}`}
      autoPlay
      loop
      muted
      playsInline
      style={{
        transform: `translate(${offset.x}px, ${offset.y}px)`,
        filter: `blur(${blur}px)`,
        transition: "transform 0.1s ease-out, filter 0.25s ease-out"
      }}
    >
      <source src={src} type="video/webm" />
    </video>
  );
}
