import { useState, useEffect } from "react";
import "./stackburger.css";

export default function StackBurgerGame() {
  // blocul ini»õial centrat perfect: (350 - 200) / 2 = 75
  const [blocks, setBlocks] = useState([
    { width: 200, left: 75, bottom: 0 }
  ]);

  const [currentLeft, setCurrentLeft] = useState(0);
  const [direction, setDirection] = useState(1);
  const [gameOver, setGameOver] = useState(false);

  const BLOCK_HEIGHT = 25;
  const MAX_VISIBLE_HEIGHT = 300;

  // mi»ôcare st√¢nga-dreapta
  useEffect(() => {
    if (gameOver) return;

    const interval = setInterval(() => {
      setCurrentLeft(prev => {
        let next = prev + direction * 3;

        if (next > 300) setDirection(-1);
        if (next < 0) setDirection(1);

        return next;
      });
    }, 10);

    return () => clearInterval(interval);
  }, [direction, gameOver]);

  function dropBlock() {
    const last = blocks[blocks.length - 1];

    const diff = Math.abs(currentLeft - last.left);

    // GAME OVER
    if (diff > last.width) {
      setGameOver(true);
      sendScore(blocks.length - 1);
      return;
    }

    // creƒÉm noul bloc tƒÉiat corect
    const newWidth = last.width - diff;
    const newLeft = Math.max(currentLeft, last.left);

    const newBlock = {
      width: newWidth,
      left: newLeft,
      bottom: last.bottom + BLOCK_HEIGHT
    };

    let newBlocks = [...blocks, newBlock];

    // dacƒÉ turnul este prea sus ‚Üí cobor√¢m totul
    if (newBlock.bottom > MAX_VISIBLE_HEIGHT) {
      newBlocks = newBlocks.map(b => ({
        ...b,
        bottom: b.bottom - BLOCK_HEIGHT
      }));
    }

    setBlocks(newBlocks);
  }

  async function sendScore(score) {
    const token = localStorage.getItem("shift_token");

    await fetch("http://localhost:5000/api/save-score", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token
      },
      body: JSON.stringify({ score })
    });
  }

  function restart() {
    setBlocks([{ width: 200, left: 75, bottom: 0 }]);
    setCurrentLeft(0);
    setGameOver(false);
  }

  return (
    <div className="stack-container">
      <h3>üçî Stack The Burger</h3>

      <div className="stack-area">

        {/* blocuri deja stivuite */}
        {blocks.map((b, i) => (
          <img
            key={i}
            src={i === 0 ? "/bun.png" : "/Meat.png"}
            className="burger-block"
            style={{
              width: b.width,
              left: b.left,
              bottom: b.bottom
            }}
          />
        ))}

        {/* bloc √Æn mi»ôcare (acum CORECT pozi»õionat √Æn interior) */}
        {!gameOver && (
          <img
            src="/Meat.png"
            className="burger-block moving"
            style={{
              width: blocks[blocks.length - 1].width,
              left: currentLeft,
              bottom:
                blocks[blocks.length - 1].bottom + BLOCK_HEIGHT + 5
            }}
          />
        )}
      </div>

      {/* butoane */}
      {!gameOver ? (
        <button className="stack-btn" onClick={dropBlock}>
          DROP
        </button>
      ) : (
        <>
          <p className="over-text">Game Over</p>
          <button className="stack-btn" onClick={restart}>
            Restart
          </button>
        </>
      )}
    </div>
  );
}
