import { useState } from "react";
import "./authmodal.css";

export default function AuthModal({ onClose, onSuccess }) {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  // üçî GenereazƒÉ o parolƒÉ fun
  function generatePass() {
    const pass = Math.random().toString(36).slice(2, 10);
    setPassword(pass);
  }

  async function submitForm(e) {
    e.preventDefault();

    const endpoint = isLogin ? "/api/auth/login" : "/api/auth/signup";

    const res = await fetch(`http://localhost:5000${endpoint}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    });

    const data = await res.json();

    if (!res.ok) {
      alert(data.error);
      return;
    }

    // salvƒÉm userul
    localStorage.setItem("shift_username", username);
    localStorage.setItem("shift_token", data.token);

    onSuccess();
    onClose();
  }

  return (
    <div className="auth-overlay">
      <div className="auth-box">

        <div className="close-btn" onClick={onClose}>Close</div>

        <div className="auth-title">
          {isLogin ? "Login" : "Sign Up"}
        </div>

        <form onSubmit={submitForm}>

          <input
            className="auth-input"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

<div
  className="password-wrapper"
  onMouseEnter={() => setShowPassword(true)}
  onMouseLeave={() => setShowPassword(false)}
>
  <input
    className="auth-input"
    type={showPassword ? "text" : "password"}
    placeholder="Password"
    value={password}
    onChange={(e) => setPassword(e.target.value)}
  />

  <span className="show-pass-hint">üëÅ Hover to reveal</span>
</div>



          {/* doar la signup apare generatorul */}
          {!isLogin && (
            <button type="button" className="pass-gen-btn" onClick={generatePass}>
              üçî Generate Funny Password
            </button>
          )}

          <button className="auth-btn" type="submit">
            {isLogin ? "Login" : "Sign Up"}
          </button>

        </form>

        <div
          className="auth-switch"
          onClick={() => {
            setIsLogin(!isLogin);
            setPassword("");
          }}
        >
          {isLogin
            ? "Need an account? Sign up"
            : "Already have an account? Login"}
        </div>

      </div>
    </div>
  );
}
